function randomHexColor(){return"#"+Math.floor(16777215*Math.random()).toString(16)}function drawWallpaper(e,r){var a=Snap("#wallpaper"),t=["white",randomHexColor(),randomHexColor(),randomHexColor()];a.clear();for(var n=0;101>n;n++)for(var o=0;101>o;o++){var l=n*e/100,i=o*r/100,p=Math.round(Math.pow(l,2)+Math.pow(i,2)),d=p%t.length;if(d>0){var w=a.rect(l+"px",i+"px",e/100+"px",r/100+"px");w.attr({fill:t[d]})}}}function resizeWallpaper(){var e=document.getElementById("wallpaper");e.style.width=window.innerWidth,e.style.height=parseInt(window.innerHeight)+4+"px",drawWallpaper(parseInt(e.style.width),parseInt(e.style.height))}window.addEventListener("load",function(){window.addEventListener("resize",resizeWallpaper,!1),resizeWallpaper()});